import React from 'react';
import {useSelector} from 'react-redux';
import moment from 'moment';
import {verilerStates, stateTarih} from '../../../features/veriler/verilerSlice';
import Spinner from '../../UI/Spinner';


export default function GunlukVeriler() {


    const veriData = useSelector(verilerStates);
    const tarih = useSelector(stateTarih);
    const date = moment(tarih).format("Do MMMM YYYY");

    return (

            <div className="modal fade" id="exampleModal" tabIndex="-1"  
                aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog modal-dialog-centered modal-dialog-scrollable">
                    <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title" 
                        id="exampleModalLabel"> 
                        <span style={{color:"tomato"}}> {tarih ? date : null}Â </span> 
                        Tarihli Veriler </h5>  
                        <button type="button" className="close" 
                        data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                        </button>
                    </div>
                    <div className="modal-body">
                    <ul className="list-group list-group-flush">
                        {veriData ? veriData.map(item=> {
                        return(<div className="row d-flex list-group-item" key={item._id}> 
                                    <div className="col-4 text-capitalize" style={{color:"darkgray"}}> {item.title} </div>   
                                    <div className="col-8 text-capitalize"> : {item.name} {item.surName} </div>  
                                </div>)
                        }) :
                        <Spinner/> }
                    </ul>
                    </div>
                    <div className="modal-footer">
                        <button type="button" 
                        className="btn btn-secondary btn-sm" 
                        data-dismiss="modal">Kapat</button>
                    </div>
                    </div>
                </div>
            </div>
    )
}
