import React, {useState, useEffect} from 'react';

import { useDispatch, useSelector } from 'react-redux';

import { setTarihteBugunToDatabase, stateTarihItem, 
    stateCompleted, tarihBilgileriUpdate} from '../../../features/veriler/tarihSlice';


import InputText from '../inputUI/InputText';
import SelectedDate from '../../components/date/SelectedDate';
import TarihteBugunList from './TarihteBugunList';


export default function VerilerCreate() {
    const dispatch = useDispatch();
    const tarihItem = useSelector(stateTarihItem);
    const completed = useSelector(stateCompleted);
    const [startDate, setStartDate] = useState(new Date());
    const [veri, setVeri] = useState({title:'', info:''})
    const {title, info} = veri;
    const onHandleVeri = name => event => {
        setVeri({...veri, [name]: event.target.value})
    }

    useEffect(() => {
        if (tarihItem) {
            setVeri({title: tarihItem.title, info: tarihItem.info});
            setStartDate(new Date(`${tarihItem.month}/${tarihItem.day}/${tarihItem.year}`))
        } else if (completed) {
            setVeri({title: "", info: ""});
        }

    }, [tarihItem, completed])


    const onSubmitHandler = (e) => {
        e.preventDefault();
        const payload = {
            title, info, date: startDate
        }
        if(tarihItem) {
            dispatch(tarihBilgileriUpdate(tarihItem._id, payload));

        } else {
          dispatch(setTarihteBugunToDatabase(payload));  
        }

    }


    return (
        <div className="container my-5" style={{marginLeft:"-10px", marginRight:"-10px"}}>
            <section className="pb-2" style={{borderBottom:"2px solid darkgray"}}> 
            <h2 className="display-5 text-gray"> Yeni Tarih Verisi GiriÅi/GÃ¼ncelleme </h2>
            <form onSubmit={onSubmitHandler}>
                <div className="row mt-3">
                <div className="col-12 col-lg-6 px-0">
                    <InputText 
                            col="col"
                            id="baslik"
                            onChangeHandler={onHandleVeri("title")} 
                            val={title} 
                            label="BaÅlÄ±k"
                            type="text"/>
                    </div>
                    <div className="col-12 col-lg-6 my-2">
                        <div>
                           <label htmlFor="selectedDa"> Tarih </label> 
                        </div>
                        <SelectedDate
                            onChangeHandler={(date) => setStartDate(date)} 
                            date={startDate} 
                            id="selectedDa"/>
                    </div>
                </div>   
                <div className="row mt-3">
                    <div className="col-12 px-0">
                        <InputText 
                                metin="true"
                                col="col"
                                id="metin"
                                onChangeHandler={onHandleVeri("info")} 
                                val={info} 
                                label="AÃ§Ä±klama"
                                type="textarea"
                            />
                    </div>
                </div> 
                <div className="row mt-3 mx-auto">
                    <div className="col-12 px-0">
                    <button type="submit" className="btn btn-warning btn-block"> 
                        {tarihItem ? "GÃ¼ncelle" : "Kaydet" }</button>
                    </div>
                </div>
            </form>
            </section>
            <TarihteBugunList/>
        </div>



    )
}
