import React, { useEffect, useState } from 'react';
import moment from 'moment';
import { useDispatch, useSelector } from 'react-redux';
import {verilerStates, stateTarih, getVeriler} from '../../../features/veriler/verilerSlice';
import Spinner from '../../UI/Spinner';

export default function VeriData() {

    const dispatch = useDispatch();
    const [toggle, setToggle] = useState(false);
    const [toggleEl, setToggleEl] = useState(false);
    const veriData = useSelector(verilerStates);
    const tarih = useSelector(stateTarih);
    const date = moment(tarih).format("Do MMMM YYYY");

    useEffect(() => {
        dispatch(getVeriler(new Date(tarih)));
    }, [dispatch, tarih]);

    const [openState, setOpenState] = useState();

    //useEffect(()=>{
    //   if(veriData){
    //       veriData.map(item => {
     //       return setOpenState({
      //          ...openState, [item._id]: false})
      //      }); 
    //    }
    //        
    //},[veriData]);

    const {...ids} = openState;
    let itempos;
    const changePos = (id:any) => {
        const values = Object.values(openState);
        if(ids[id]) {
            setOpenState({...openState, [id]: false});
        } else {
            setOpenState({...openState, [id]: true});
            itempos = values.find(item => item === true);
            if(itempos){
            setToggleEl(true)
            }else{
            setToggleEl(false)
            }
        }

    }

    const toggleHandler = () => {
        if(toggleEl){
            setOpenState({})
        }
        setToggle(!toggle)
    };

    const yayin = veriData ? 
        veriData.map(item => {       
            return(
                    <div className="list-group mt-3" key = {item._id} style={{marginRight:"0"}}>
                        <div className="list-group-item list-group-item-action text-secondary" 
                            style={{backgroundColor: "#f2f2eb", paddingRight:"0"}}>
                            {item._id}
                            <span style={{float:"right"}}>
                                <button className="btn btn-sm mx-5"
                                    onClick={()=> {changePos(item._id)}}> 
                                    <i className= {ids[item._id] ? "fas fa-angle-up fa-2x" :
                                     "fas fa-angle-down fa-2x"}  style={{color:"red"}}></i>  
                                </button>
                            </span>
                            {item.elemans.length > 0 && ids[item._id] || toggle ?
                            <ul className="list-group mt-2" style={{listStyle:"none", float:"none"}}>
                                {item.elemans.map(el => 
                                    <li className="list-item d-flex"> 
                                        <span className="text-capitalize col-6" 
                                            style={{color: "tan", marginLeft:"-0.3rem"}}> 
                                            {el.mission} 
                                        </span> 
                                        <span className="text-capitalize col-6"  
                                        style={{color: "#2968bf"}}> 
                                            : {el.name} <span className="text-uppercase">{el.surName} </span> </span> 
                                    </li>
                                )}

                            </ul> : null}
                        </div>
                    </div>
            ) 
        }) : 
        <div className="mx-auto">
           <Spinner/>  
        </div>


        return (
            <div>
                <h5 className="text-center text-gray"> {date} Tarihli Veriler 
                        <button style={{float:"right", border:"none", display:"flex",
                        backgroundColor:"inherit", alignItems:"center"}}
                            onClick={() => toggleHandler()}> 
                            <span className="px-2">
                                {toggle || toggleEl ? " Kapat " : "Tümünü Aç "}
                            </span> 
                            <i className= { toggle || toggleEl ? "fas fa-caret-up fa-2x" :
                                            "fas fa-caret-down fa-2x"}  style={{color:"red"}}></i>
                        </button>
                </h5>
                <div style={{float:"unset", padding:"0", border:"none"}}>
                  {yayin}   
                </div>

            </div>
        )
        ;

}
