import React, {useState, useEffect} from 'react';

import { useDispatch, useSelector } from 'react-redux';

import { setTarihteBugunToDatabase, stateTarihItem, 
    stateCompleted, tarihBilgileriUpdate} from '../../../features/veriler/tarihSlice';


import InputText from '../inputUI/InputText';
import SelectedDate from '../../components/date/SelectedDate';
import TarihteBugunList from './TarihteBugunList';
import Heading from '../inputUI/Heading';
import Button from '../inputUI/Button';


const TarihteBugun: React.FC = () => {
    const dispatch = useDispatch();

    const [startDate, setStartDate] = useState(new Date());
    const [veri, setVeri] = useState({title:'', info:''})
    const {title, info} = veri;


    ///// redux


    const onHandleVeri = (name:string)=> (event:any) => {
        setVeri({...veri, [name]: event.target.value})
    }

    useEffect(() => {
        if (tarihItem._id !== '') {
            setVeri({title: tarihItem.title, info: tarihItem.info});
            setStartDate(new Date(`${tarihItem.month}/${tarihItem.day}/${tarihItem.year}`))
        } else if (completed) {
            setVeri({title: "", info: ""});
        }

    }, [tarihItem._id, completed])


    const onSubmitHandler = (e:any) => {
        e.preventDefault();
        let payload = {_id:'',user:'', title, info, date: startDate}
        if(tarihItem._id !== '') {
            payload = {_id:tarihItem._id, user:tarihItem.user, title, info, date: startDate}
            dispatch(tarihBilgileriUpdate(tarihItem._id, payload, startDate));
        } else {
            dispatch(setTarihteBugunToDatabase(payload));  
        }   
    }


    return (
        <div className="container" style={{marginLeft:"-10px", marginRight:"-10px"}}>
            <section className="pb-2" style={{borderBottom:"2px solid darkgray"}}> 
            <Heading text="Yeni Tarih Verisi GiriÅi/GÃ¼ncelleme"/>
            <form onSubmit={onSubmitHandler}>
                <div className="row mt-3">
                <div className="col-12 col-lg-6 px-0">
                    <InputText 
                        col="col"
                        id="baslik"
                        onChangeHandler={onHandleVeri("title")} 
                        val={title} 
                        label="BaÅlÄ±k"
                        type="text"/>
                    </div>
                    <div className="col-12 col-lg-6 my-2">
                        <div>
                           <label htmlFor="selectedDa"> Tarih </label> 
                        </div>
                        <SelectedDate
                            onChangeHandler={(date) => setStartDate(date)} 
                            date={startDate} 
                            id="tarihteBugun"/>
                    </div>
                </div>   
                <div className="row mt-3">
                    <div className="col-12 px-0">
                        <InputText 
                            metin={true}
                            col="col"
                            id="metin"
                            onChangeHandler={onHandleVeri("info")} 
                            val={info} 
                            label="AÃ§Ä±klama"
                            type="textarea"
                        />
                    </div>
                </div> 
                <Button text ={tarihItem._id !== '' ? "GÃ¼ncelle" : "Kaydet" } />
            </form>
            </section>
            <TarihteBugunList/>
        </div>



    )
}

export default TarihteBugun;
