import React from 'react';
import DatePicker, {tr} from 'react-datepicker'
import './SelectedDate.css';

export default function SelectedDate({onChangeHandler, date, minDate, maxDate, id}) {

    return (
        <DatePicker
          id={id}
          className="example-custom-input"
          dateFormat="dd/MM/yyyy"
          selected={date}
          onChange={onChangeHandler}
          tabIndex={1}
          minDate={minDate ? minDate : null}
          maxDate={maxDate ? maxDate : null}
          showMonthDropdown
          showYearDropdown
          locale={tr}
          dropdownMode="select"
          popperClassName="some-custom-class"
            popperPlacement="top-end"
            popperModifiers={{
                offset: {
                enabled: true,
                offset: "15px, 10px"
                },
                preventOverflow: {
                enabled: true,
                escapeWithReference: false,
                boundariesElement: "viewport"
                }
            }}
       />
    );
}
