import React, {useState, useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux';
import Selector from '../inputUI/Selector';
import {getVerilerOzel, verilerOzelStates, 
    deleteVeriler, setUpdateEL} from '../../../features/veriler/verilerSlice';

import { stateKategoriler } from '../../../features/veriler/kategorilerSlice';

export default function VerilerList() {

    const [veri, setVeri] = useState({category:'', mission:''});
    const {category, mission} = veri;
    const dispatch = useDispatch();
    const verilerData = useSelector(verilerOzelStates);
    const kategoriDatas = useSelector(stateKategoriler);
    let kategorilerim = [];
    kategoriDatas.map(item => kategorilerim.push(item.category));

    const [missionEls, setMissionEls] = useState([]);

    useEffect(()=> {
        if(category !== '') {
            const el = kategoriDatas.find(item => item.category === category);
            setMissionEls(el.missions)  
        } 
    }, [category])

    useEffect(() => {
        dispatch(getVerilerOzel(category, mission));
    },[mission, dispatch])

    const onHandleVeri = name => event => {
        setVeri({...veri, [name]: event.target.value})
    }


    let veriler = verilerData ? verilerData.map(item => {
        return(
            <tbody>
                <tr>
                    <td> { item.name } </td>
                    <td> { item.surName } </td>
                    <td> { new Date(item.start).toISOString().slice(0,10) } </td>
                    <td> { new Date(item.end).toISOString().slice(0,10) } </td>
                    <td style={{justifyContent:"space-around"}}> 
                        <button
                            onClick={() => dispatch(setUpdateEL(item._id))}>
                            <i className="far fa-edit"></i>
                        </button>
                        <button 
                            onClick={()=>{dispatch(deleteVeriler(item._id, category, mission))}}> 
                        <i className="far fa-trash-alt"></i> 
                        </button>
                    </td>
                </tr>
            </tbody>)}): null;

    return (
        <section className="my-3">
            <h2 className="display-5 text-gray" > Verileri Listele </h2>
            <div className="row">
                <div className="col-md-6">
                    <Selector 
                        onHandleVeri={onHandleVeri("category")} 
                        item={category} 
                        items={kategorilerim}/>
                </div>
                <div className="col-md-6">
                    <Selector 
                        onHandleVeri={onHandleVeri("mission")}
                        item={mission} 
                        items={missionEls}/>
                </div>
            </div>
            <div className="row">
                <div className="col-12" style={{overflowX:"auto"}}>
                <table className="table table-striped dt-responsive nowrap w-100 text-center">
                    <thead>
                        <tr>
                            <th>Ad</th><th>Soyad</th><th>Grv. Başlangıç</th>
                            <th> Grv. Bitiş</th><th> İşlemler </th>
                        </tr>
                    </thead>
                    {veriler}
                </table>
                </div>
            </div>
        </section>
    )
}
