import React, {useState, useEffect} from 'react';

import { useDispatch, useSelector } from 'react-redux';
import { setVerilerToDatabase, stateUpdateEl, stateVeri,stateCompleted,
    updateVeriler, stateMessage, setMessage, stateErrors, stateUpdateId } 
    from '../../../features/veriler/verilerSlice';


import { getKategoriler, stateKategoriler } from '../../../features/veriler/kategorilerSlice';

import '../../components/date/SelectedDate.css';
import Selector from '../inputUI/Selector';
import Tarih from '../inputUI/Tarih';
import InputText from '../inputUI/InputText';
import VerilerList from './VerilerList';
import Tooltip from './Tooltip';
import Heading from '../inputUI/Heading';
import Button from '../inputUI/Button';

const VerilerCreate = () => {

    const dispatch = useDispatch();
    //// state
    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());
    const [veri, setVeri] = useState({name:'', surName:'', mission:'', category:''});
    const {name, surName, mission, category} = veri;
    ////// redux

    const updateEl = useSelector(stateUpdateEl);
    const updateId = useSelector(stateUpdateId);
    const errorsDatabase = useSelector(stateErrors);
    const completed = useSelector(stateCompleted);
    const message = useSelector(stateMessage);
    const data = useSelector(stateVeri);







    const onHandleVeri = (name:string) => (event:any) => {
        setVeri({...veri, [name]: event.target.value})
    }

    ///// Kategori Part
    useEffect(() => {
        dispatch(getKategoriler());
    }, [dispatch]);

    const kategoriDatas = useSelector(stateKategoriler);
    let kategorilerim:Array<string> = [];
    const [missionEls, setMissionEls] = useState<Array<string>>([]);

    kategoriDatas.map(item => kategorilerim.push(item.category));

    useEffect(()=> {
        if(category !== '') {
            const el = kategoriDatas.find(item => item.category === category);
            if(el){
               //setMissionEls(el.missions)   
            }
        } 
    }, [category])

    /////// Error Part

    const [errors, setErrors] = useState({nam:'', surNam:'', categor:'', missio:''});
    const {nam, surNam, categor, missio} = errors;

    useEffect(() => {
        setErrors({...errorsDatabase});
    },[errorsDatabase])

    ///// update part


    useEffect(() => {
        if (updateId !== '') {
            setVeri({name: updateEl.name, surName: updateEl.surName, 
                category: updateEl.category, mission: updateEl.mission});
            setStartDate(new Date(updateEl.start));
            setEndDate(new Date(updateEl.end))
        }
    }, [updateEl])

    useEffect(() => {
        if(completed) {
           setVeri({name: '', surName: '', category: '', mission: ''});
            setStartDate(new Date());
            setEndDate(new Date()) 
        }

    },[completed])


    /// submit part 

    const onSubmitHandler = (e:any) => {
        e.preventDefault();
        let payload = { _id: '', user: '', category, mission, name, surName, 
            start: startDate.toISOString().split('T')[0], 
            end: endDate.toISOString().split('T')[0] }
        if(updateEl) {
            payload = {
                _id: updateEl._id, user:updateEl.user ,category, mission, name, surName, 
                start: startDate.toISOString().split('T')[0], 
                end: endDate.toISOString().split('T')[0]
            }
            dispatch(updateVeriler(updateEl._id, payload, category, mission))
        } else {
           dispatch(setVerilerToDatabase(payload)); 
        } 
    }


    return (
        <div className="container" style={{marginLeft:"-10px", marginRight:"-10px"}}>
            <Tooltip 
                message={message} 
                onDeleteMessage = {()=> dispatch(setMessage())}
                data= {data}
            />
            <section className="pb-2" style={{borderBottom:"2px solid darkgray"}}> 
                <Heading text="Yeni Veri GiriÅi/GÃ¼ncelleme" />
                <form onSubmit={onSubmitHandler}>
                    <div className="row align-items-center">
                        <div className="col-md-6">
                            <Selector
                                title="Kategori"
                                items={kategorilerim} 
                                item={category}
                                onHandleVeri={onHandleVeri("category")}
                                error={categor}/>
                        </div>
                        <div className="col-md-6">
                            <Selector
                                title="GÃ¶revler"
                                items={missionEls} 
                                item={mission} 
                                onHandleVeri={onHandleVeri("mission")}
                                error={missio}/>
                        </div>
                    </div> 
                    <div className="row mt-3">
                            <div className="col-12 col-lg-6 px-0">
                                <InputText
                                col="col"
                                id="ad"
                                onChangeHandler={onHandleVeri("name")} 
                                val={name} 
                                label="Ad"
                                type="text"
                                error={nam}
                                />
                            </div>
                            <div className="col-12 col-lg-6 px-0">
                                <InputText
                                    col="col"
                                    id="soyad"
                                    onChangeHandler={onHandleVeri("surName")} 
                                    val={surName} 
                                    label="Soyad"
                                    type="text"
                                    error={surNam}
                                    />
                            </div>
                        </div>
                        <Tarih 
                            startDate={startDate} 
                            endDate={endDate} 
                            onChangeHandlerStart={(date) => setStartDate(date)}
                            onChangeHandlerEnd={(date) => setEndDate(date)}
                        />
                        <Button text = {updateEl._id !== '' ? 'GÃ¼ncelle': 'Kaydet'}/>

                </form>
            </section>
            <VerilerList/>
        </div>


    )
}

export default VerilerCreate;
