import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import { AppThunk, RootState } from '../../app/store';
import axios from 'axios';

interface Datas {
  id: string;
  user: string;
  category: string;
  mission: string;
  name: string;
  surName: string;
  start: Date;
  end: Date;
}

interface VerilerState {
  veriler:Array <Datas>;
  veri:Datas;
  verilerOzel:Array <Datas>;
  errors:{};
  gosterilen: number;
  isCompleted: boolean;
  updateEl:Datas | undefined;
  message:'';
  tarih: Date;
};

const el = { id:'', user:'', category:'', mission:'', name:'', surName:'', start:new Date(), end:new Date()}

const initialState: VerilerState = {
  veriler: [],
  veri: el,
  verilerOzel:[],
  errors:{},
  gosterilen: 1, 
  isCompleted: false,
  updateEl:el,
  message:'',
  tarih: new Date()
};

export const verilerSlice = createSlice({
  name: 'veriler',
  initialState,
  reducers: {
    setInitial:(state) => {
      state.updateEl = el;
      state.isCompleted = false;  
      state.errors = {};
    },
    setMessage:(state) => {
      state.message = '';
      state.veri = el;
    },
    setVeriler:(state, action: PayloadAction<Array<Datas>>) => {
      state.veriler = action.payload;
      state.isCompleted = true;
    },
    setVeri:(state, action: PayloadAction<Datas>) => {
      state.veri = action.payload;
      state.isCompleted = true;
    },
    setVerilerOzel:(state, action: PayloadAction<Array<Datas>>) => {
      state.verilerOzel = action.payload;
      state.isCompleted = true;
    },
    setDataFailed: (state, action: PayloadAction<Datas>) => {
      state.errors = action.payload;
      state.isCompleted = false;
    },
    setGosterilen:(state, action: PayloadAction<number>) => {
      state.gosterilen = action.payload;
    },
    setUpdateEL:(state, action: PayloadAction<string>) => {
      const eleman = state.verilerOzel.find(item => item.id === action.payload);
      state.updateEl = eleman;
    },
    setVeriTarih:(state, action: PayloadAction<Date>) => {
      state.tarih = action.payload;
    }

  },
});

export const { setVeriler, setVeri, setDataFailed, setUpdateEL,
  setVerilerOzel, setGosterilen, setInitial, setMessage, setVeriTarih } = verilerSlice.actions;


//// verileri getiriyor

export const getVeriler = (date: Date): AppThunk  => dispatch => {
    const dat = new Date(date).toISOString().split("T")[0];
    axios.get(`http://localhost:5000/api/veriler?date=${date}`)
    .then(res => {
        dispatch(setVeriler(res.data));
        dispatch(setVeriTarih(new Date(dat)))
    })
    .catch(err =>{
        dispatch(setDataFailed(err))
    })
};

export const getVerilerOzel = (category: string, mission: string): AppThunk => dispatch => {
  axios.get(`http://localhost:5000/api/veriler/ozel?category=${category}&mission=${mission}`)
  .then(res => {
      dispatch(setVerilerOzel(res.data))
  })
  .catch(err =>{
      dispatch(setDataFailed(err))
  })
};

export const setVerilerToDatabase = (data: Datas ): AppThunk => dispatch => {
  const token = localStorage.getItem('token');
  console.log(data);
  axios.post('http://localhost:5000/api/veriler/create', data,
  {headers: {"Authorization": `Bearer ${token}`}})
  .then(res => {
    dispatch(setVeri(res.data));
    dispatch(setInitial());
  })
  .catch(err =>{
    dispatch(setDataFailed(err.response.data))
  })
};


export const deleteVeriler = (id: string, category: string, mission: string): AppThunk => dispatch => {
  const token = localStorage.getItem('token');
  axios.delete(`http://localhost:5000/api/veriler/${id}`,
  {headers: {"Authorization": `Bearer ${token}`}})
  .then(res => {
      dispatch(getVerilerOzel(category, mission))
  })
  .catch(err =>{
      dispatch(setDataFailed(err))
  })
};

export const updateVeriler = (id: string, data: Datas, category: string, mission: string): AppThunk => dispatch => {
  const token = localStorage.getItem('token');
  axios.put(`http://localhost:5000/api/veriler/update/${id}`, data,
  {headers: {"Authorization": `Bearer ${token}`}})
  .then(res => {
      dispatch(getVerilerOzel(category, mission));
      dispatch(setInitial());
  })
  .catch(err =>{
      dispatch(setDataFailed(err.response.data))
  })
};


export const stateVeri = (state: RootState) => state.veriler.veri;
export const verilerStates = (state: RootState) => state.veriler.veriler;
export const verilerOzelStates = (state: RootState) => state.veriler.verilerOzel;
export const stateGoster = (state: RootState) => state.veriler.gosterilen;
export const stateUpdateEl = (state: RootState) => state.veriler.updateEl;
export const stateCompleted = (state: RootState) => state.veriler.isCompleted;
export const stateMessage = (state: RootState) => state.veriler.message;
export const stateTarih = (state: RootState) => state.veriler.tarih;
export const stateErrors = (state: RootState) => state.veriler.errors;


export default verilerSlice.reducer;
